sourcesContent:
import { useDispatch } from 'react-redux';

// material-ui
import { Button, Grid } from '@mui/material';

// project import
import MainCard from 'components/MainCard';
import ComponentHeader from 'components/cards/ComponentHeader';
import ComponentWrapper from 'sections/components-overview/ComponentWrapper';
import ComponentSkeleton from 'sections/components-overview/ComponentSkeleton';
import { openSnackbar } from 'store/reducers/snackbar';

// ==============================|| COMPONENTS - SNACKBAR ||============================== //

const ComponentSnackbar = () => {
  const dispatch = useDispatch();

  const basicSnackbarCodeString = `<Button
  variant="contained"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is default message',
        variant: 'alert',
        close: false
      })
    )
  }
>
  Default
</Button>
<Button
  variant="contained"
  color="secondary"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is secondary message',
        variant: 'alert',
        alert: { color: 'secondary' },
        close: false
      })
    )
  }
>
  Secondary
</Button>
<Button
  variant="contained"
  color="success"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is success message',
        variant: 'alert',
        alert: {
          color: 'success'
        },
        close: false
      })
    )
  }
>
  Success
</Button>
<Button
  variant="contained"
  color="warning"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is warning message',
        variant: 'alert',
        alert: {
          color: 'warning'
        },
        close: false
      })
    )
  }
>
  Warning
</Button>
<Button
  variant="contained"
  color="info"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is info message',
        variant: 'alert',
        alert: {
          color: 'info'
        },
        close: false
      })
    )
  }
>
  Info
</Button>
<Button
  variant="contained"
  color="error"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is error message',
        variant: 'alert',
        alert: {
          color: 'error'
        },
        close: false
      })
    )
  }
>
  Error
</Button>`;

  const outlinedSnackbarCodeString = `<Button
  variant="outlined"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is default message',
        variant: 'alert',
        alert: {
          variant: 'outlined'
        },
        close: false
      })
    )
  }
>
  Default
</Button>
<Button
  variant="outlined"
  color="secondary"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is secondary message',
        variant: 'alert',
        alert: {
          variant: 'outlined',
          color: 'secondary'
        },
        close: false
      })
    )
  }
>
  Secondary
</Button>
<Button
  variant="outlined"
  color="success"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is success message',
        variant: 'alert',
        alert: {
          variant: 'outlined',
          color: 'success'
        },
        close: false
      })
    )
  }
>
  Success
</Button>
<Button
  variant="outlined"
  color="warning"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is warning message',
        variant: 'alert',
        alert: {
          variant: 'outlined',
          color: 'warning'
        },
        close: false
      })
    )
  }
>
  Warning
</Button>
<Button
  variant="outlined"
  color="info"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is info message',
        variant: 'alert',
        alert: {
          variant: 'outlined',
          color: 'info'
        },
        close: false
      })
    )
  }
>
  Info
</Button>
<Button
  variant="outlined"
  color="error"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is error message',
        variant: 'alert',
        alert: {
          variant: 'outlined',
          color: 'error'
        },
        close: false
      })
    )
  }
>
  Error
</Button>`;

  const closeSnackbarCodeString = `<Button
  variant="contained"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is default message',
        variant: 'alert'
      })
    )
  }
>
  Default
</Button>
<Button
  variant="contained"
  color="secondary"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is secondary message',
        variant: 'alert',
        alert: { color: 'secondary' }
      })
    )
  }
>
  Secondary
</Button>
<Button
  variant="contained"
  color="success"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is success message',
        variant: 'alert',
        alert: {
          color: 'success'
        }
      })
    )
  }
>
  Success
</Button>
<Button
  variant="contained"
  color="warning"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is warning message',
        variant: 'alert',
        alert: {
          color: 'warning'
        }
      })
    )
  }
>
  Warning
</Button>
<Button
  variant="contained"
  color="info"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is info message',
        variant: 'alert',
        alert: {
          color: 'info'
        }
      })
    )
  }
>
  Info
</Button>
<Button
  variant="contained"
  color="error"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is error message',
        variant: 'alert',
        alert: {
          color: 'error'
        }
      })
    )
  }
>
  Error
</Button>`;

  const actionSnackbarCodeString = `<Button
  variant="contained"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is default message',
        variant: 'alert',
        actionButton: true
      })
    )
  }
>
  Default
</Button>
<Button
  variant="contained"
  color="secondary"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is secondary message',
        variant: 'alert',
        alert: { color: 'secondary' },
        actionButton: true
      })
    )
  }
>
  Secondary
</Button>
<Button
  variant="contained"
  color="success"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is success message',
        variant: 'alert',
        alert: {
          color: 'success'
        },
        actionButton: true
      })
    )
  }
>
  Success
</Button>
<Button
  variant="contained"
  color="warning"
  onClick={() =>
    dispatch(
      openSnackbar({
        open: true,
        message: 'This is warning message',
        variant: 'alert',
        alert: {
